/**
 * Config Optinino framework
 */
function otino_option($field) {
    return optionino_get('otino_option',$field);
}
define('OPTNNO_TEXTDOMAIN', 'optionino');
function add_custom_styles_otino() {
    if (isset($_GET['page'])) {
        if ($_GET['page'] == "otino_option_settings" || $_GET['page'] == "otino-user-export") {
            ?>
            <style>
                :root {
                    --optionino-main-color: #0CC0DF !important;
                }
            </style>
            <?php
            wp_enqueue_style('otino-admin-style', OTINO_ASSETS . '/css/admin-otino.css', array(), OTINO_VERSION , 'all' );
        }
    }
}
add_action('admin_enqueue_scripts','add_custom_styles_otino');

